 * @param target An integer representing the target number.
 * @return A list of pairs representing the indices of the numbers in the list that sum up to the target.
 *         Each pair contains the index of the number and its value.
 *
 * Example:
 * findSum(listOf(1, 2, 3, 4, 5), 7) -> listOf(Pair(0, 1), Pair(1, 2), Pair(2, 3), Pair(3, 4))
 */
fun findSum(numbers: List<Int>, target: Int): List<Pair<Int, Int>> {
    val result = mutableListOf<Pair<Int, Int>>()
    for (i in numbers.indices) {
        for (j in i + 1 until numbers.size) {
            if (numbers[i] + numbers[j] == target) {
                result.add(Pair(i, numbers[i]))
                result.add(Pair(j, numbers[j]))
            }
        }
    }
    return result
}

