/**
 * This function takes a list of integers and sorts it in ascending order using the quick sort algorithm.
 *
 * @param arr A list of integers.
 */
fun quickSort(arr: MutableList<Int>) {
    // Check if the input is a list
    if (arr !is MutableList<Int>) {
        throw IllegalArgumentException("Input must be a list.")
    }

    // Check if the list is empty
    if (arr.isEmpty()) {
        return
    }

    // Call the inner function to perform the sorting
    quickSortInner(arr, 0, arr.size - 1)
}

/**
 * This function performs the actual sorting of the list using the quick sort algorithm.
 *
 * @param arr The list of integers to be sorted.
 * @param start The starting index of the sublist to be sorted.
 * @param end The ending index of the sublist to be sorted.
 */
fun quickSortInner(arr: MutableList<Int>, start: Int, end: Int) {
    // Base case: if the sublist has only one element, it is already sorted
    if (start == end) {
        return
    }

    // Partition the list using the pivot element
    val pivot = arr[end]
    var i = start
    var j = start

    while (j < end) {
        if (arr[j] < pivot) {
            // Swap the elements at indices i and j
            val temp = arr[i]
            arr[i] = arr[j]
            arr[j] = temp

            // Increment i to move to the next element
            i++
        }

        // Increment j to move to the next element
        j++
    }

    // Swap the pivot element with the element at index i
    val temp = arr[i]
    arr[i] = arr[end]
    arr[end] = temp

    // Recursively sort the sublists before and after the pivot element
    quickSortInner(arr, start, i - 1)
    quickSortInner(arr, i + 1, end)
}

