/**
 * Given a list of strings representing the steps required to complete a task,
 * find the minimal number of steps required to complete the task.
 *
 * @param steps A list of strings representing the steps required to complete the task.
 * @return The minimal number of steps required to complete the task.
 */
fun minimalSteps(steps: List<String>): Int {
    var stepsCount = 0
    var stepIndex = 0

    while (stepIndex < steps.size) {
        val step = steps[stepIndex]

        // Check if the current step is a loop
        if (step.startsWith("loop")) {
            val loopSteps = step.substring(4).toInt()

            // Check if the loop requires more steps
            if (loopSteps > stepsCount) {
                stepsCount = loopSteps
            }
        }

        stepIndex++
    }

    return stepsCount
}

