/**
 * Given two integers n and m, find the number of unique paths through a grid of size n x m.
 *
 * @param n The number of rows in the grid.
 * @param m The number of columns in the grid.
 * @return The number of unique paths through the grid.
 */
fun uniquePaths(n: Int, m: Int): Int {
    // Initialize a 2D array to store the number of unique paths for each cell
    val grid = Array(n) { IntArray(m) }

    // Base case: if either n or m is zero, there is only one unique path
    if (n == 0 || m == 0) {
        return 1
    }

    // Iterate over each cell in the grid
    for (i in 0 until n) {
        for (j in 0 until m) {
            // If the cell is on the edge of the grid, there is only one unique path
            if (i == 0 || j == 0) {
                grid[i][j] = 1
            } else {
                // Calculate the number of unique paths for the current cell
                grid[i][j] = grid[i - 1][j] + grid[i][j - 1]
            }
        }
    }

    // Return the number of unique paths at the bottom-right corner of the grid
    return grid[n - 1][m - 1]
}

