                val wordChain = mutableListOf<String>()
                var length = 0

                for (char1 in word1) {
                    for (char2 in word2) {
                        if (char1 == char2) {
                            wordChain.add(char1.toString())
                            length++
                        }
                    }
                }

                if (length > maxLength) {
                    maxLength = length
                    longestWordChain.clear()
                    longestWordChain.addAll(wordChain)
                }
            }
        }
    }

    return longestWordChain
}

/**
 * Given a number of quantities and a list of quantities, find the maximum quantity that can be distributed among the farmers.
 *
 * @param quantities The number of quantities.
 * @param farmerQuantities A list of quantities.
 * @return The maximum quantity that can be distributed among the farmers.
 */
fun minimizedMaximum(quantities: Int, farmerQuantities: List<Int>): Int {
    var maxQuantity = 0

    for (quantity in farmerQuantities) {
        if (quantity > maxQuantity) {
            maxQuantity = quantity
        }
    }

    return maxQuantity
}

fun main() {
    val quantities = 15
    val farmerQuantities = listOf(10, 5, 7, 3, 12, 2, 9, 1, 4, 6, 8, 11, 13, 14)

    val result = minimizedMaximum(quantities, farmerQuantities)
    println("The maximum quantity that can be distributed among the farmers: $result")
}
