/**
 * This function takes in a positive integer n and returns the sum of all positive integers less than or equal to n.
 *
 * @param n a positive integer
 * @return the sum of positive integers less than or equal to n
 */
fun positiveIntegersSum(n: Int): Int {
var sum = 0
    var i = 1
    while (i <= n) {
        sum += i
        i++
    }
    return sum
}

