/**
 * This function calculates the salary of a social worker based on their monthly salary and years of experience.
 *
 * @param monthlySalary the social worker's monthly salary
 * @param yearsOfExperience the number of years of experience the social worker has
 * @return the calculated salary of the social worker
 */
fun calculateSocialWorkerSalary(monthlySalary: Double, yearsOfExperience: Int): Double {
return when {
        yearsOfExperience >= 10 -> monthlySalary
        yearsOfExperience >= 5 -> monthlySalary * 1.5
        yearsOfExperience >= 2 -> monthlySalary * 2
        else -> monthlySalary * 3
    }
}

