/**
 * This function takes a list of bandit arms and a list of rewards received for each arm.
 * It returns the name of the arm that has the highest expected reward, considering the reward is greater than or equal to the threshold.
 *
 * @param arms A list of strings representing the names of the bandit arms.
 * @param rewards A list of floats representing the rewards received for each arm.
 * @return The name of the arm with the highest expected reward, or null if no arm satisfies the threshold condition.
 */
fun findBestArm(arms: List<String>, rewards: List<Float>): String? {
    val threshold = 0.8f  // Minimum expected reward threshold

    var bestArm: String? = null
    var highestReward = Float.NEGATIVE_INFINITY  // Initialize with negative infinity

    for (i in arms.indices) {
        val arm = arms[i]
        val reward = rewards[i]

        if (reward >= threshold && reward > highestReward) {
            highestReward = reward
            bestArm = arm
        }
    }

    return bestArm
}

