/**
 * Given a positive integer n, calculate the sum of all numbers from 1 to n that are divisible by both 5 and 7.
 * Note: You should not include numbers that are divisible by 35 (both 5 and 7) twice in the sum.
 *
 * Example:
 * calculateSums(100) -> 6339
 */
fun calculateSums(n: Int): Int {
var totalSum = 0

    var num = 1
    while (num <= n) {
        if (num % 5 == 0 && num % 7 == 0) {
            if (num % 35 != 0) {
                totalSum += num
            }
        }
        num++
    }

    return totalSum
}

