object AocMap {
    val INSTANCE = Map()

    fun solve() {
        INSTANCE.solve()
    }
}

class Map {
    fun solve() {
        val input = readInput()
        val map = input.map { it.split(" -> ") }.map {
            val (from, to) = it
            val (fromX, fromY) = from.split(",")
            val (toX, toY) = to.split(",")
            Pair(
                Point(fromX.toInt(), fromY.toInt()),
                Point(toX.toInt(), toY.toInt())
            )
        }.toMap()

        val points = map.keys.toList()
        val pointsWithDistance = points.map {
            val (from, to) = map[it]!!
            val distance = from.distance(to)
            Pair(it, distance)
        }.sortedBy { it.second }

        val closestPoints = pointsWithDistance.take(2)
        val closestPointsDistance = closestPoints.map { it.second }.sum()
        println("Closest points: $closestPointsDistance")
    }

    private fun readInput(): List<String> {
        return File("input.txt").readLines()
    }
}

data class Point(val x: Int, val y: Int) {
    fun distance(other: Point): Int {
        return Math.abs(x - other.x) + Math.abs(y - other.y)
    }
}

