/**
 * Given three numbers, this function finds and returns the maximum number
 * using nested conditional statements and the "not equal" operator.
 *
 * @param num1 The first number.
 * @param num2 The second number.
 * @param num3 The third number.
 * @return The maximum number among the three given numbers.
 */
fun findMax(num1: Int, num2: Int, num3: Int): Int {
var maxNum = num1  // Assuming num1 is the maximum number

    // Check if num2 is greater than maxNum
    if (num2 > maxNum) {
        maxNum = num2

        // Check if num3 is greater than maxNum
        if (num3 > maxNum) {
            maxNum = num3
        }
    }
    // Check if num3 is greater than maxNum
    else if (num3 > maxNum) {
        maxNum = num3
    }

    return maxNum
}

