 *
 * @param n A positive integer
 * @return True if n is prime, False otherwise
 */
fun isPrime(n: Int): Boolean {
    if (n <= 1) {
        return false
    }

    if (n == 2) {
        return true
    }

    if (n % 2 == 0) {
        return false
    }

    for (i in 3..(Math.sqrt(n.toDouble()).toInt() + 1 step 2)) {
        if (n % i == 0) {
            return false
        }
    }

    return true
}

