/**
 * This exercise requires the completion of a basic while loop syntax that counts the number of negative values in a given range.
 *
 * @param start Starting value of the range
 * @param end Ending value of the range
 * @return Number of negative values in the given range
 */
fun countNegative(start: Int, end: Int): Int {
var count = 0  // Initialize the count of negative values as 0

    var current = start
    while (current <= end) {
        // Check if the current value is negative
        if (current < 0) {
            count++  // Increment the count if the value is negative
        }
        current++  // Increment the current value
    }

    return count
}

