 * This function calculates the sum of the first n natural numbers.
 *
 * @param n The number of natural numbers to be summed up.
 * @return The sum of the first n natural numbers.
 */
fun calculateSum(n: Int): Int {
var sum = 0
    var i = 1
    while (true) {
        sum += i
        i++
        if (i > n) {
            break
        }
    }
    return sum
}

